processInfo-summary-view.map:
function (doc) {
  if (doc.type == "processInfo")
    emit([doc.processYmd, doc.lotNo, doc._id], [1, parseInt(doc.lotNoTotalWeight), parseInt(doc.processWeight), parseInt(doc.purchasingCost), parseInt(doc.sellingPrice)]);
}

processable-pigs-view.map:
function (doc) {
  if ((doc.type == 'pig' && doc.processed == false) || doc.type == 'pigLotNo') {
      emit(doc._id, null)
  }
}

pigs-by-unprocessed-traceNo-pigNo-view.map:
function (doc) {
  if (doc.type == "pig" && doc.processed == false)
    emit([doc.traceNo, doc.pigNo], doc.processed);
}
